import React, { useState, useEffect, useRef } from 'react';
import '../styles/kkomap.scss';

const KakaoMapSearch = () => {
  const [map, setMap] = useState(null);
  const [scriptLoaded, setScriptLoaded] = useState(false);
  const addressRef = useRef(null);
  const mapContainerRef = useRef(null);

  // 카카오맵 API 스크립트를 동적으로 로드하고, 로드가 완료되면 지도 생성을 호출합니다.
  useEffect(() => {
    if (!document.getElementById('kakao-map-script')) {
      const script = document.createElement('script');
      script.id = 'kakao-map-script';
      script.src = 'https://dapi.kakao.com/v2/maps/sdk.js?appkey=80d68b9c1c97f9edd72ef5e13725bb51&libraries=services';
      script.async = true;
      script.onload = () => {
        setScriptLoaded(true);
        // SDK가 완전히 로드된 후에 최초 지도를 생성할 수 있습니다.
        // 예를 들어, 컴포넌트가 마운트될 때 기본 지도를 표시하고 싶다면 여기서 호출합니다.
        createMap();
      };
      document.body.appendChild(script);
    } else {
      setScriptLoaded(true);
    }
  }, []);

  // 지도를 생성하는 함수 (SDK가 로드된 후에만 호출됨)
  const createMap = () => {
    if (!scriptLoaded) {
      alert('카카오맵 스크립트가 아직 로드되지 않았습니다.');
      return;
    }
    // window.kakao가 준비되었는지 확인
    if (!window.kakao || !window.kakao.maps) {
      alert('카카오맵 SDK가 준비되지 않았습니다.');
      return;
    }
    const { kakao } = window;
    const container = mapContainerRef.current;
    container.style.display = 'block';
    const options = {
      center: new kakao.maps.LatLng(37.566826, 126.9786567),
      level: 5,
    };
    const newMap = new kakao.maps.Map(container, options);
    setMap(newMap);
    newMap.relayout();
    return newMap;
  };

  // 검색 결과의 각 장소에 대해 마커를 생성합니다.
  const displayMarker = (place, currentMap) => {
    const { kakao } = window;
    const marker = new kakao.maps.Marker({
      map: currentMap,
      position: new kakao.maps.LatLng(place.y, place.x),
    });
    kakao.maps.event.addListener(marker, 'click', function () {
      const infowindow = new kakao.maps.InfoWindow({ zIndex: 1 });
      infowindow.setContent(
        `<div style="padding:5px;font-size:12px;">${place.place_name}</div>`
      );
      infowindow.open(currentMap, marker);
    });
  };

  // 검색 결과를 처리하고 지도 범위를 설정합니다.
  const placesSearchCB = (data, status, pagination, currentMap) => {
    const { kakao } = window;
    if (status === kakao.maps.services.Status.OK) {
      const bounds = new kakao.maps.LatLngBounds();
      data.forEach(place => {
        displayMarker(place, currentMap);
        bounds.extend(new kakao.maps.LatLng(place.y, place.x));
      });
      currentMap.setBounds(bounds);
      currentMap.relayout();
    } else {
      alert('검색 결과가 없습니다.');
    }
  };

  // 검색 버튼 클릭 핸들러
  const handleSearch = () => {
    const address = addressRef.current.value.trim();
    if (!address) {
      alert('주소를 입력하세요.');
      return;
    }
    if (!scriptLoaded) {
      alert('카카오맵 스크립트가 아직 로드되지 않았습니다.');
      return;
    }
    const { kakao } = window;
    const currentMap = map ? map : createMap();
    const ps = new kakao.maps.services.Places();
    ps.keywordSearch(address, (data, status, pagination) =>
      placesSearchCB(data, status, pagination, currentMap)
    );
  };

  return (
    <div>
      <div className="map">카카오맵 장소 검색</div>
      <div className="container">
        <div className="search-box">
          <input
            type="text"
            id="address"
            ref={addressRef}
            placeholder="주소를 입력하세요"
          />
          <button id="searchButton" onClick={handleSearch}>
            검색
          </button>
        </div>
        <div
          id="map"
          ref={mapContainerRef}
          style={{ width: '100%', height: '400px' }}
        ></div>
      </div>
    </div>
  );
};

export default KakaoMapSearch;
