import React, { useEffect, useRef, useState } from 'react';
import '../styles/kkomap.scss';
import shopData from '../data/nail_salon.json'; // import 사용

const ShopLocation = () => {
  const mapRef = useRef(null);
  const [shopData, setShopData] = useState([]);

  useEffect(() => {
    // 1. JSON 파일 로딩
    fetch() // JSON 파일 경로
      .then(res => res.json())
      .then(data => setShopData(shopData))
      .catch(error => console.error('Error loading JSON:', error));
  }, []);

  useEffect(() => {
    const createMap = () => {
      if (window.kakao && window.kakao.maps && mapRef.current && shopData.length > 0) {
        const container = mapRef.current;
        const options = {
          center: new window.kakao.maps.LatLng(shopData[0].latitude, shopData[0].longitude), // 첫 번째 네일샵 위치를 지도의 중심으로 설정
          level: 3,
        };
        const map = new window.kakao.maps.Map(container, options);

        // 2. 마커 표시
        shopData.forEach(shop => {
          const marker = new window.kakao.maps.Marker({
            map: map,
            position: new window.kakao.maps.LatLng(shop.latitude, shop.longitude),
            title: shop.name, // 마커에 네일샵 이름 표시
          });
        });
      }
    };

    const loadScript = () => {
      const script = document.createElement('script');
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=80d68b9c1c97f9edd72ef5e13725bb51&libraries=services&autoload=false`; // libraries=services 추가
      script.async = "true";
      document.head.appendChild(script);

      script.onload = () => {
        window.kakao.maps.load(() => {
          // 3. 주소 기반 GeoCoding
          Promise.all(
            shopData.map(shop => {
              return new Promise((resolve, reject) => {
                const geocoder = new window.kakao.maps.services.Geocoder();
                geocoder.addressSearch(shop.address, (result, status) => {
                  if (status === window.kakao.maps.services.Status.OK) {
                    resolve({
                      ...shop,
                      latitude: result[0].y,
                      longitude: result[0].x,
                    });
                  } else {
                    console.error(`${shop.name} GeoCoding error: ${status}`);
                    resolve(shop); // GeoCoding 실패 시 기존 정보 유지
                  }
                });
              });
            })
          ).then(updatedShopData => {
            setShopData(updatedShopData);
            createMap();
          });
        });
      };

      return () => {
        document.head.removeChild(script);
      };
    };

    if (!window.kakao) {
      loadScript();
    } else {
      window.kakao.maps.load(() => {
        createMap();
      });
    }
  }, [shopData]);

  return (
    <div>
      <h2>JUNGWON-NAILART SHOP</h2>
      <div className='map'>
        <div ref={mapRef} style={{ width: '700px', height: '600px' }}></div>
      </div>
    </div>
  );
};

export default ShopLocation;
