import React, { useEffect, useRef } from 'react';
import '../styles/kkomap.scss';

const SalonLocation = () => {
  const mapRef = useRef(null);
  const [shopData, setShopData] = useState([]);

  useEffect(() => {
    // 1. JSON 파일 로딩
    fetch('/nail_salon.ㅓ내ㅜ') // JSON 파일 경로
      .then(res => res.json())
      .then(data => setShopData(data))
      .catch(error => console.error('Error loading JSON:', error));
  }, []);

  useEffect(() => {
    const createMap = () => {
      if (window.kakao && window.kakao.maps && mapRef.current) { // mapRef.current 유효성 검사 추가
        const container = mapRef.current;
        // ... 지도 초기화 코드 ...
        const options = {
          center: new window.kakao.maps.LatLng(33.450701, 126.570667),
          level: 3,
        };
        new window.kakao.maps.Map(container, options);
      }
    };

    const loadScript = () => {
      const script = document.createElement('script');
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=80d68b9c1c97f9edd72ef5e13725bb51&autoload=false`;
      script.async = "true";
      document.head.appendChild(script);

      script.onload = () => {
        window.kakao.maps.load(() => {
          createMap();
        });
      };

      return () => {
        document.head.removeChild(script);
      };
    };

    if (!window.kakao) {
      loadScript();
    } else {
      window.kakao.maps.load(() => {
        createMap();
      });
    }
  }, []);

  return (
    <div>
      <h2>JUNGWON-NAILART SALON</h2> 
      <div className='map'>   
      <div ref={mapRef} style={{ width: '700px', height: '600px' }}></div>      
      </div>
    </div>
  );
};

export default SalonLocation;
