import React, { useState, useEffect, useRef } from 'react';
import '../styles/kkomap.scss';

const KakaoApp = () => {
  // 로그인 관련 상태
  const [loginScriptLoaded, setLoginScriptLoaded] = useState(false);
  const [token, setToken] = useState('');

  // 지도 관련 상태
  const [map, setMap] = useState(null);
  const [mapScriptLoaded, setMapScriptLoaded] = useState(false);
  const addressRef = useRef(null);
  const mapContainerRef = useRef(null);

  // 1. 로그인 SDK (Kakao JS SDK) 동적 로드 및 초기화
  useEffect(() => {
    if (!document.getElementById('kakao-js-sdk')) {
      const script = document.createElement('script');
      script.id = 'kakao-js-sdk';
      script.src = 'https://t1.kakaocdn.net/kakao_js_sdk/2.7.4/kakao.min.js';
      script.integrity = 'sha384-DKYJZ8NLiK8MN4/C5P2dtSmLQ4KwPaoqAfyA/DfmEc1VDxu4yyC7wy6K1Hs90nka';
      script.crossOrigin = 'anonymous';
      script.async = true;
      script.onload = () => {
        if (window.Kakao && !window.Kakao.isInitialized()) {
          window.Kakao.init('c089c8172def97eb00c07217cae17495');
        }
        setLoginScriptLoaded(true);
        displayToken();
      };
      document.body.appendChild(script);
    } else {
      setLoginScriptLoaded(true);
      displayToken();
    }
  }, []);

  // 2. 지도 SDK (Kakao Map SDK) 동적 로드
  useEffect(() => {
    if (!document.getElementById('kakao-map-script')) {
      const script = document.createElement('script');
      script.id = 'kakao-map-script';
      script.src = 'https://dapi.kakao.com/v2/maps/sdk.js?appkey=80d68b9c1c97f9edd72ef5e13725bb51&libraries=services';
      script.async = true;
      script.onload = () => setMapScriptLoaded(true);
      document.body.appendChild(script);
    } else {
      setMapScriptLoaded(true);
    }
  }, []);

  // 로그인 버튼 클릭 시 호출되는 함수
  const loginWithKakao = () => {
    if (!loginScriptLoaded) {
      alert('Kakao 로그인 SDK가 아직 로드되지 않았습니다.');
      return;
    }
    window.Kakao.Auth.authorize({
      redirectUri: 'https://developers.kakao.com/tool/demo/oauth',
    });
  };

  // 쿠키에서 토큰을 추출하여 상태에 반영하는 함수
  const displayToken = () => {
    const tokenFromCookie = getCookie('authorize-access-token');
    if (tokenFromCookie) {
      window.Kakao.Auth.setAccessToken(tokenFromCookie);
      window.Kakao.Auth.getStatusInfo()
        .then((res) => {
          if (res.status === 'connected') {
            setToken(window.Kakao.Auth.getAccessToken());
          }
        })
        .catch((err) => {
          window.Kakao.Auth.setAccessToken(null);
          setToken('');
        });
    }
  };

  // 단순 쿠키 추출 함수
  const getCookie = (name) => {
    const parts = document.cookie.split(name + '=');
    if (parts.length === 2) return parts[1].split(';')[0];
    return '';
  };

  // 지도 생성 함수
  const createMap = () => {
    if (!mapScriptLoaded) {
      alert('카카오맵 스크립트가 아직 로드되지 않았습니다.');
      return;
    }
    const { kakao } = window;
    const container = mapContainerRef.current;
    container.style.display = 'block';
    const options = {
      center: new kakao.maps.LatLng(37.566826, 126.9786567),
      level: 5,
    };
    const newMap = new kakao.maps.Map(container, options);
    setMap(newMap);
    newMap.relayout();
    return newMap;
  };

  // 검색 결과의 각 장소에 대해 마커를 생성하는 함수
  const displayMarker = (place, currentMap) => {
    const { kakao } = window;
    const marker = new kakao.maps.Marker({
      map: currentMap,
      position: new kakao.maps.LatLng(place.y, place.x),
    });
    kakao.maps.event.addListener(marker, 'click', function () {
      const infowindow = new kakao.maps.InfoWindow({ zIndex: 1 });
      infowindow.setContent(
        `<div style="padding:5px;font-size:12px;">${place.place_name}</div>`
      );
      infowindow.open(currentMap, marker);
    });
  };

  // 장소 검색 결과를 처리하는 콜백 함수
  const placesSearchCB = (data, status, pagination, currentMap) => {
    const { kakao } = window;
    if (status === kakao.maps.services.Status.OK) {
      const bounds = new kakao.maps.LatLngBounds();
      for (let i = 0; i < data.length; i++) {
        displayMarker(data[i], currentMap);
        bounds.extend(new kakao.maps.LatLng(data[i].y, data[i].x));
      }
      currentMap.setBounds(bounds);
      currentMap.relayout();
    } else {
      alert('검색 결과가 없습니다.');
    }
  };

  // 검색 버튼 클릭 핸들러
  const handleSearch = () => {
    const address = addressRef.current.value.trim();
    if (!address) {
      alert('주소를 입력하세요.');
      return;
    }
    if (!mapScriptLoaded) {
      alert('카카오맵 스크립트가 아직 로드되지 않았습니다.');
      return;
    }
    const { kakao } = window;
    const currentMap = map ? map : createMap();
    const ps = new kakao.maps.services.Places();
    ps.keywordSearch(address, (data, status, pagination) =>
      placesSearchCB(data, status, pagination, currentMap)
    );
  };

  return (
    <div>
      <h2>카카오 로그인</h2>
      <a
        id="kakao-login-btn"
        onClick={loginWithKakao}
        style={{ cursor: 'pointer', display: 'inline-block' }}
      >
        <img
          src="https://k.kakaocdn.net/14/dn/btroDszwNrM/I6efHub1SN5KCJqLm1Ovx1/o.jpg"
          width="222"
          alt="카카오 로그인 버튼"
        />
      </a>
      <p id="token-result">
        {token ? `login success, token: ${token}` : ''}
      </p>

      <hr />

      <h2>카카오맵 장소 검색</h2>
      <div className="container">
        <div className="search-box">
          <input
            type="text"
            id="address"
            ref={addressRef}
            placeholder="주소를 입력하세요"
          />
          <button id="searchButton" onClick={handleSearch}>
            검색
          </button>
        </div>
        <div
          id="map"
          ref={mapContainerRef}
          style={{ width: '100%', height: '400px' }}
        ></div>
      </div>
    </div>
  );
};

export default KakaoApp;
