import React, { useEffect, useRef, useState } from 'react';
import '../styles/kkomap.scss';

const ShopLocation = () => {
  const mapRef = useRef(null);
  const [shopData, setShopData] = useState(null);

  useEffect(() => {
    fetch('/data/nail_salon.json')
      .then(res => res.json())
      .then(data => setShopData(data))
      .catch(error => console.error('Error loading JSON:', error));
  }, []);

  useEffect(() => {
    const createMap = () => {
      if (window.kakao && window.kakao.maps && mapRef.current && shopData && shopData.length > 0) {
        const container = mapRef.current;
        const options = {
          center: new window.kakao.maps.LatLng(shopData[0].latitude || 33.450701, shopData[0].longitude || 126.570667), // 기본 좌표 설정
          level: 3,
        };
        const map = new window.kakao.maps.Map(container, options);

        shopData.forEach(shop => {
          if (shop.latitude && shop.longitude) {
            new window.kakao.maps.Marker({
              map: map,
              position: new window.kakao.maps.LatLng(shop.latitude, shop.longitude),
              title: shop.name,
            });
          }
        });
      }
    };

    const loadScript = () => {
      const script = document.createElement('script');
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=80d68b9c1c97f9edd72ef5e13725bb51&libraries=services&autoload=false`;
      script.async = "true";
      document.head.appendChild(script);

      script.onload = () => {
        window.kakao.maps.load(() => {
          if (shopData) {
            Promise.all(
              shopData.map(shop => {
                return new Promise((resolve, reject) => {
                  const geocoder = new window.kakao.maps.services.Geocoder();
                  geocoder.addressSearch(shop.address, (result, status) => {
                    if (status === window.kakao.maps.services.Status.OK) {
                      resolve({
                        ...shop,
                        latitude: result[0].y,
                        longitude: result[0].x,
                      });
                    } else {
                      console.error(`${shop.name} GeoCoding error: ${status}`);
                      resolve({
                        ...shop,
                        latitude: null,
                        longitude: null,
                      });
                    }
                  });
                });
              })
            ).then(updatedShopData => {
              setShopData(updatedShopData);
              createMap();
            });
          }
        });
      };

      return () => {
        document.head.removeChild(script);
      };
    };

    if (!window.kakao) {
      loadScript();
    } else if (shopData) { // shopData가 있을 때만 createMap 호출
      createMap();
    }
  }, [shopData]);

  return (
    <div>
      <h2>JUNGWON-NAILART SHOP</h2>
      <div className='map'>
        <div ref={mapRef} style={{ width: '700px', height: '600px' }}></div>
      </div>
    </div>
  );
};

export default ShopLocation;
