import React, { useState, useEffect } from "react";
import "../styles/slider.scss"; // 슬라이더 스타일 적용
import "../styles/TextTyping.scss"; // SCSS 파일 연결

const Home = () => {
  const slidesData = [
    {
      id: 1,
      title: "고객의 마음을 이해하는 정원아트네일",
      content: "JUNGWON NAIL ART",
      bgImage: "/shop_01.webp",
    },
    {
      id: 2,
      title: "No1. Nail Art Creation Company",
      content: "JUNGWON NAIL ART",
      bgImage: "/shop_02.webp",
    },
  ];

  const [currentSlide, setCurrentSlide] = useState(0);
  const totalSlides = slidesData.length;

  useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % totalSlides);
    }, 3000); // 3초마다 변경

    return () => clearInterval(slideInterval);
  }, [totalSlides]);

  const [scrollY, setScrollY] = useState(window.scrollY);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // 진행률 계산 함수
  const calWidth = (scroll, start, end) => {
    if (scroll < start) return 0;
    if (scroll > end) return 100;
    return ((scroll - start) / (end - start)) * 100;
  };

  return (
    <div>
      {/* 슬라이더 섹션 */}
      <section className="slider">
        {slidesData.map((slide, index) => (
          <div
            key={slide.id}
            className={`slide ${index === currentSlide ? "active" : ""}`}
            style={{ backgroundImage: `url(${slide.bgImage})` }}
          >
            <div className="overlay">
              <h2 className="title">{slide.title}</h2>
              <p className="content">{slide.content}</p>
            </div>
          </div>
        ))}
      </section>

      {/* 텍스트 타이핑 섹션 */}
      <div id="TextoutBox">
        <div className="text">
          <span>신뢰와 열정으로 네일아트의&nbsp;</span>
          <p style={{ width: `${calWidth(scrollY, 482, 595)}%` }}>
            신뢰와 열정으로 네일아트의
          </p>
        </div>
        <div className="text">
          <span>미래를 선도합니다.</span>
          <p style={{ width: `${calWidth(scrollY, 596, 709)}%` }}>
            미래를 선도합니다.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Home;
