import React, { useEffect, useRef, useState } from 'react';
import '../styles/kkomap.scss';

const ShopLocation = () => {
  const mapRef = useRef(null);
  const [shopData, setShopData] = useState(null);

  useEffect(() => {
    // 1. JSON 파일 로딩
    fetch('/data/nail_salon.json') // 절대 경로 사용
      .then(res => res.json())
      .then(data => setShopData(data)) // shopData 상태 업데이트
      .catch(error => console.error('Error loading JSON:', error));
  }, []);

  useEffect(() => {
    const createMap = () => {
      if (window.kakao && window.kakao.maps && mapRef.current && shopData && shopData.length > 0 && shopData[0].latitude && shopData[0].longitude) { // 모든 조건 충족 시
        const container = mapRef.current;
        const options = {
          center: new window.kakao.maps.LatLng(shopData[0].latitude, shopData[0].longitude), // shopData가 존재하는지 확인
          level: 3,
        };
        const map = new window.kakao.maps.Map(container, options);

        // 2. 마커 표시
        shopData.forEach(shop => {
          if (shop.latitude && shop.longitude) { // 위도/경도가 존재하는지 확인
            const marker = new window.kakao.maps.Marker({
              map: map,
              position: new window.kakao.maps.LatLng(shop.latitude, shop.longitude),
              title: shop.name, // 마커에 네일샵 이름 표시
            });
          }
        });
      }
    };

    const loadScript = () => {
      const script = document.createElement('script');
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=80d68b9c1c97f9edd72ef5e13725bb51&libraries=services&autoload=false`; // libraries=services 추가
      script.async = "true";
      document.head.appendChild(script);

      script.onload = () => {
        window.kakao.maps.load(() => {
          if (shopData) { // shopData가 null이 아닐 때만 GeoCoding 실행
            // 3. 주소 기반 GeoCoding
            Promise.all(
              shopData.map(shop => {
                return new Promise((resolve, reject) => {
                  const geocoder = new window.kakao.maps.services.Geocoder();
                  geocoder.addressSearch(shop.address, (result, status) => {
                    if (status === window.kakao.maps.services.Status.OK) {
                      resolve({
                        ...shop,
                        latitude: result[0].y,
                        longitude: result[0].x,
                      });
                    } else {
                      console.error(`${shop.name} GeoCoding error: ${status}`);
                      resolve({ // GeoCoding 실패 시에도 shop 객체는 반환
                        ...shop,
                        latitude: null, // 위도/경도 값은 null로 설정
                        longitude: null,
                      });
                    }
                  });
                });
              })
            ).then(updatedShopData => {
              setShopData(updatedShopData);
              createMap(); // GeoCoding 완료 후 createMap 호출
            });
          }
        });
      };

      return () => {
        document.head.removeChild(script);
      };
    };

    if (!window.kakao) {
      loadScript();
    } else {
      window.kakao.maps.load(() => {
        createMap();
      });
    }
  }, [shopData]);

  return (
    <div>
      <h2>JUNGWON-NAILART SHOP</h2>
      <div className='map'>
        <div ref={mapRef} style={{ width: '700px', height: '600px' }}></div>
      </div>
    </div>
  );
};


export default ShopLocation;
