import React, { useEffect, useRef, useState } from 'react';
import '../styles/kkomap.scss';

const ShopLocation = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    const createMap = () => {
      if (window.kakao && window.kakao.maps && mapRef.current) { // mapRef.current 유효성 검사 추가 (DOM 렌더링 후 지도 초기화)
        const container = mapRef.current;
        const options = {
          center: new window.kakao.maps.LatLng(33.450701, 126.570667),
          level: 3,
        };
        new window.kakao.maps.Map(container, options);
      }
    };

    const loadScript = () => {
      const script = document.createElement('script');
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=80d68b9c1c97f9edd72ef5e13725bb51&autoload=false`; // **YOUR_APP_KEY** 부분을 실제 API 키로 대체하세요!
      script.async = true; // 비동기 로딩 설정
      document.head.appendChild(script);

      script.onload = () => {
        window.kakao.maps.load(() => {
          createMap(); // 카카오맵 API 로딩 완료 후 지도 초기화 함수 호출
        });
      };

      return () => {
        document.head.removeChild(script); // 컴포넌트 언마운트 시 스크립트 제거 (메모리 누수 방지)
      };
    };

    if (!window.kakao) {
      loadScript(); // window.kakao 객체가 없으면 (API 스크립트 미로드) 스크립트 로딩 시작
    } else {
      window.kakao.maps.load(() => {
        createMap(); // window.kakao 객체가 있으면 (API 스크립트 이미 로드됨) 바로 지도 초기화
      });
    }
  }, []); // 빈 의존성 배열: 컴포넌트 마운트 시에만 useEffect 훅 실행

  return (
    <div>
      <h2>JUNGWON-NAILART SHOP</h2>
      <div className='map'>
        <div ref={mapRef} style={{ width: '700px', height: '600px' }}></div>
      </div>
    </div>
  );
};

export default ShopLocation;
