import React, { useEffect, useState } from 'react';
import '../styles/VideoSection.scss';

const VideoSection = () => {
  const [hide, setHide] = useState(true);
  const [topPrev, setTopPrev] = useState(0);
  const movieTop = 300; // 스크롤 위치 기준(필요에 따라 조정)

  useEffect(() => {
    const handleWheel = () => {
      const topNow = window.scrollY;
      const delta = topNow - topPrev;
      const bgChangeEl = document.getElementById('bgChange');
      const videoEl = document.querySelector('#movie video');

      if (topNow > 100 && delta > 0 && hide && bgChangeEl) {
        bgChangeEl.classList.remove('hide');
      }
      if (topNow > 300 && delta > 0 && hide && videoEl) {
        videoEl.classList.add('big');
        setHide(false);
      } else if (topNow <= movieTop && delta < 0 && !hide && bgChangeEl && videoEl) {
        videoEl.classList.remove('big');
        bgChangeEl.classList.add('hide');
        setHide(true);
      }
      setTopPrev(topNow);
    };

    window.addEventListener('wheel', handleWheel);
    return () => window.removeEventListener('wheel', handleWheel);
  }, [topPrev, hide]);

  return (
    <section id="bgChange" className="hide">
      <div>
        <div className="sec4Text">
          <p>
            MAIL ART
            <br />
            CREATION COMPANY
          </p>
          <p>고객의 마음을 이해하는 정원네일아트</p>
        </div>
        <div id="movie">
          <video autoPlay loop muted controls>
            <source src="https://sora.com/g/gen_01jmtywkm7efcvdzaktjn6h844" type="video/mp4" />
          </video>
        </div>
      </div>
    </section>
  );
};

export default VideoSection;
